import {useRef} from "react";
import styles from "./Checkout.module.css";

const isEmpty=(value)=> value.trim() === '';

const Checkout = (props) => {
    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postalInputRef = useRef();
    const cityInputRef = useRef();

    let isNameValid = true;
    let isstreetValid = true;
    let isPostalValid = true;
    let isCityValid = true;
    
    const submitHandler = (e) => {
    e.preventDefault();
    const name = nameInputRef.current.value;
    const street = streetInputRef.current.value;
    const postal = postalInputRef.current.value;
    const city = cityInputRef.current.value;

    isNameValid = !isEmpty(name);
    isstreetValid = !isEmpty(street);
    isPostalValid = !isEmpty(postal);
    isCityValid = !isEmpty(city);

    const isFormValid = isNameValid && isstreetValid && isPostalValid && isCityValid;

    if(!isFormValid){

        return;
    }

    console.log("submitted.........");
  };

  return (
    <form onSubmit={submitHandler} className={styles.form}>
      <div className={styles.control}>
        <label htmlFor="name">Your Name:</label>
        <input ref={nameInputRef} type="text" id="name" />
        {isNameValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="street">Street:</label>
        <input ref={streetInputRef} type="text" id="street" />
        {isstreetValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="postal">Postal Code:</label>
        <input ref={postalInputRef}  type="text" id="postal" />
        {isPostalValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="city">City:</label>
        <input  ref={cityInputRef} type="text" id="city" />
        {isCityValid && <p>Please enter a valid name</p>}
      </div>

      <div className={styles.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button type="submit" style={{backgroundColor:"pink"}}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
