import {useRef, useState} from "react";
import styles from "./Checkout.module.css";

const isEmpty=(value) => value.trim() === '';

const Checkout = (props) => {
    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postalInputRef = useRef();
    const cityInputRef = useRef();


    const [formValid,setFormValid] = useState({isNameValid:true,isstreetValid:true,isPostalValid:true,isCityValid:true});
    
    const submitHandler = (e) => {
    e.preventDefault();
    const name = nameInputRef.current.value;
    const street = streetInputRef.current.value;
    const postal = postalInputRef.current.value;
    const city = cityInputRef.current.value;
    
    if(!isEmpty(name)){
        console.log("false")
        setFormValid({isNameValid:false});
    }else{
        console.log("true")
        setFormValid({isNameValid:true});
    }

    if(!isEmpty(street)){
        setFormValid({isstreetValid:false});
    }else{
        setFormValid({isstreetValid:true});
    }

    if(!isEmpty(postal)){
        setFormValid({isPostalValid:false});
    }else{
        setFormValid({isPostalValid:true});
    }

    if(!isEmpty(city)){
        setFormValid({isCityValid:false});
    }else{
        setFormValid({isCityValid:true});
    }
    // setFormValid({isNameValid = !isEmpty(name)})
    // setFormValid({isstreetValid = !isEmpty(street)});
    // setFormValid({isPostalValid = !isEmpty(postal)});
    // setFormValid({isCityValid = !isEmpty(city)});

    const isFormValid = formValid.isNameValid && formValid.isstreetValid && formValid.isPostalValid && formValid.isCityValid;

    if(!isFormValid){
        return;
    }

    console.log("submitted.........");
  };

  return (
    <form onSubmit={submitHandler} className={styles.form}>
      <div className={styles.control}>
        <label htmlFor="name">Your Name:</label>
        <input ref={nameInputRef} type="text" id="name" />
        {!formValid.isNameValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="street">Street:</label>
        <input ref={streetInputRef} type="text" id="street" />
        {!formValid.isstreetValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="postal">Postal Code:</label>
        <input ref={postalInputRef}  type="text" id="postal" />
        {!formValid.isPostalValid && <p>Please enter a valid name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="city">City:</label>
        <input  ref={cityInputRef} type="text" id="city" />
        {!formValid.isCityValid && <p>Please enter a valid name</p>}
      </div>

      <div className={styles.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button type="submit" style={{backgroundColor:"pink"}}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
