import styles from "./MealItemForm.module.css";
import Input from "../../UI/Input";

const MealItemForm = () => {
  return <form className={styles.form}>
      <h3>ADD to cart</h3>
      <Input 
      label="Amount"
      input={{
          id:"amount",
          type:"number",
          min:"1",
          max:"5",
          step:"1",
          defaultValue:"1"
      }}
      />
      <button>+Add</button>
  </form>;
};

export default MealItemForm;
