import { useContext } from "react";
import Modal from "../UI/Modal";
import styles from "./Cart.module.css";
import CartContext from "../../store/cart-context"
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  const totalAmount = `$ ${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItems = cartCtx.items.map(
    (item) => <CartItem name={props.name} amount={props.amount} price={props.price}/>
  );

  const OrderButtonHandler=()=>{
    console.log("Order is placed !!");
    props.hideCart();
  }
  return (
    <Modal onClick={props.hideCart}>
      <ul className={styles['cart-items']}>{cartItems}</ul>
      <div className={styles.total}>
          <span>Total Amount</span>
          <span>{totalAmount}</span>
      </div>
      <div className={styles.actions}>
          <button className={styles['button--alt']} onClick={props.hideCart}>Close</button>
          {hasItems && <button className={styles.button} onClick={OrderButtonHandler}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
